import { useState } from "react";
import { useMediaQuery } from "@mui/material";
import { Box, Typography, Button, Collapse } from "@mui/material";
import Navbar from "scenes/navbar";

const HelpPage = () => {
  const isNonMobileScreens = useMediaQuery("(min-width: 1000px)");
  const [expandedIndex, setExpandedIndex] = useState(null);

  const questions = [
    {
      question: "How do I sign up to get notifications?",
      answer: "",
    },
    {
      question: "How do I create an account?",
      answer:
        "To create an account, click on the 'Sign Up' button and follow the registration process.",
    },
    {
      question: "How can I contact customer support?",
      answer:
        "You can contact our customer support team by calling our helpline or sending an email to support@example.com.",
    },
  ];

  const handleToggleAnswer = (index) => {
    setExpandedIndex(index === expandedIndex ? null : index);
  };

  return (
    <Box>
      <Navbar />
      <Box
        width="100%"
        padding="2rem 6%"
        display="flex"
        flexDirection="column"
        alignItems="center"
      >
        {/* Paragraph box for website explanation */}
        <Box
          mb={4}
          textAlign="center"
          border={1}
          borderColor="primary.main"
          borderRadius={8}
          p={2}
        >
          <Typography variant="h4" gutterBottom>
            Welcome to Enroll Checker
          </Typography>
          <Typography variant="body1">
            Enroll Checker is a web application designed to assist UW-Madison
            students in securing enrollment in highly competitive classes. Our
            platform eliminates the frustration of missing out on class seat
            openings by providing real-time notifications via SMS messages or
            emails.
            <br />
            <br />
            With Enroll Checker, you can easily create an account and subscribe
            to your desired courses. Once subscribed, our system will diligently
            monitor class availability for you. As soon as a seat becomes
            available, you will promptly receive a notification, ensuring you
            never miss an opportunity to enroll in your preferred classes.
          </Typography>
        </Box>

        {/* FAQ toggle spots */}
        <Box width="100%">
          {questions.map((faq, index) => (
            <Box
              key={index}
              width="100%"
              mb={2}
              textAlign="left"
              borderRadius={8}
              border={1}
              borderColor="primary.main"
              overflow="hidden"
            >
              <Button
                variant="outlined"
                onClick={() => handleToggleAnswer(index)}
                fullWidth
                size="large"
                style={{
                  textTransform: "none",
                  justifyContent: "flex-start",
                  textAlign: "left",
                }}
              >
                <Typography variant="body1">{faq.question}</Typography>
              </Button>
              <Collapse in={expandedIndex === index}>
                <Typography variant="body1" align="left">
                  {faq.answer}
                </Typography>
              </Collapse>
            </Box>
          ))}
        </Box>
      </Box>
    </Box>
  );
};

export default HelpPage;
