import { Box, Typography, useMediaQuery } from "@mui/material";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import Navbar from "scenes/navbar";
import CoursesWidget from "scenes/widgets/CoursesWidget";
import UserWidget from "scenes/widgets/UserWidget";

const ProfilePage = () => {
  const [user, setUser] = useState(null);
  const { userId } = useParams();
  const token = useSelector((state) => state.token);
  const isNonMobileScreens = useMediaQuery("(min-width:1000px)");
  const courseList = useSelector((state) => state.courseList);

  const getUser = async () => {
    const response = await fetch(`http://localhost:3001/users/${userId}`, {
      method: "GET",
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await response.json();
    setUser(data);
  };

  useEffect(() => {
    getUser();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  if (!user) return null;

  console.log(courseList)
  const hasSubscribedCourses = courseList.length > 0;

  return (
    <Box height="100%">
      <Navbar />
      <Box
        height="100%"
        display="flex"
        flexDirection="column"
        alignItems="center"
        justifyContent="center"
      >
        <Box>
          <UserWidget userId={userId} />
          <Box m="2rem 0" />
          {!hasSubscribedCourses && (
            <Typography
              fontWeight="bold"
              color="primary"
              fontSize={isNonMobileScreens ? "clamp(1rem, 2rem, 2.25rem)" : "1.5rem"}
            >
              No subscribed courses.
            </Typography>
          )}
        </Box>
        {hasSubscribedCourses && (
          <Box mt="2rem">
            <CoursesWidget userId={userId} isProfile />
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default ProfilePage;
