import WidgetWrapper from "components/WidgetWrapper";
import { setSubscriptions } from "state";
import { useState, useEffect } from "react";

const CourseWidget = ({
  courseId,
  title,
  credits,
  termCode,
  subjectCode,
  courseID,
  courseDesignation,
  subscribed,
}) => {
  // ... your existing code ...

  return (
    <WidgetWrapper>
      <Box p="1rem" width="100%" display="flex" justifyContent="space-between">
        <Box flex="1">
          <Typography variant="h6" sx={{ color: primary }}>
            {title}
          </Typography>
          <Typography variant="body1">{`Credits: ${credits}`}</Typography>
          <Typography variant="body1">{`Course Designation: ${courseDesignation}`}</Typography>
        </Box>
        <Box>
          <Button
            variant="outlined"
            onClick={handleToggleSubscription}
            sx={{ marginTop: "1rem" }}
          >
            {isSubscribed ? "Unsubscribe" : "Subscribe"}
          </Button>
        </Box>
      </Box>
    </WidgetWrapper>
  );
};

