import React, { useState, useEffect } from "react";
import { Box, Typography, useTheme, Button } from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import WidgetWrapper from "components/WidgetWrapper";
import { setSubscriptions } from "state";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const CourseWidget = ({
  courseId,
  title,
  credits,
  termCode,
  subjectCode,
  courseID,
  courseDesignation,
  subscribed,
  description,
}) => {
  const dispatch = useDispatch();
  const token = useSelector((state) => state.token);
  const loggedInUserId = useSelector((state) => state.user._id);

  const { palette } = useTheme();
  const primary = palette.primary.main;

  const [isSubscribed, setIsSubscribed] = useState(false);

  const subscribeCourse = async () => {
    // Implementation for subscribing to the course
  };

  const unsubscribeCourse = async () => {
    // Implementation for unsubscribing from the course
  };

  const handleToggleSubscription = async () => {
    if (isSubscribed) {
      await unsubscribeCourse();
      toast.error("Course successfully unsubscribed", {
        autoClose: 1200,
      });
    } else {
      await subscribeCourse();
      toast.success("Course successfully subscribed", { autoClose: 1200 });
    }
    setIsSubscribed(!isSubscribed); // Toggle the value
  };

  useEffect(() => {
    setIsSubscribed(subscribed);
  }, [subscribed]);

  useEffect(() => {
    // Check if the current user is subscribed to the course
    const isUserSubscribed = subscribed.includes(loggedInUserId);
    setIsSubscribed(isUserSubscribed);
  }, [loggedInUserId, subscribed]);

  return (
    <WidgetWrapper>
      <Box p="1rem">
        <Typography variant="h6" sx={{ color: primary }}>
          {title}
        </Typography>
        <Typography variant="body1">{`Credits: ${credits}`}</Typography>
        <Typography variant="body1">{`Course Designation: ${courseDesignation}`}</Typography>

        <Typography variant="body1" sx={{ whiteSpace: "pre-wrap" }}>
          {`\nDescription: ${description}`}
        </Typography>

        <Button
          variant="outlined"
          onClick={handleToggleSubscription}
          sx={{ marginTop: "1rem" }}
        >
          {isSubscribed ? "Unsubscribe" : "Subscribe"}
        </Button>
      </Box>
      <ToastContainer position="bottom-right" style={{ fontSize: "20px" }} />
    </WidgetWrapper>
  );
};

export default CourseWidget;
