import { useState } from "react";
import { useMediaQuery } from "@mui/material";
import { useSelector } from "react-redux";
import { Box, Typography, Button, Collapse } from "@mui/material";
import Navbar from "scenes/navbar";

const HelpPage = () => {
  const isNonMobileScreens = useMediaQuery("(min-width: 1000px)");
  //const { _id } = useSelector((state) => state.user);
  const [expandedIndex, setExpandedIndex] = useState(null);

  const questions = [
    {
      question: "What is the purpose of this website?",
      answer:
        "The purpose of this website is to provide information about our products and services.",
    },
    {
      question: "How do I create an account?",
      answer: "To create an account, click on the 'Sign Up' button and follow the registration process.",
    },
    {
      question: "How can I contact customer support?",
      answer: "You can contact our customer support team by calling our helpline or sending an email to support@example.com.",
    },
  ];

  const handleToggleAnswer = (index) => {
    setExpandedIndex(index === expandedIndex ? null : index);
  };

  return (
    <Box>
      <Navbar />
      <Box
        width="100%"
        padding="2rem 6%"
        display={isNonMobileScreens ? "flex" : "block"}
        gap="0.5rem"
        justifyContent="space-between"
      >
        <Box flexBasis={isNonMobileScreens ? "26%" : undefined} display="flex" flexDirection="column" alignItems="center">
          {/* Paragraph box for website explanation */}
          <Box mb={4} textAlign="center">
            <Typography variant="h4" gutterBottom>
              Welcome to Our Website
            </Typography>
            <Typography variant="body1">
              This website provides information about our products and services. Explore the FAQs below for more details.
            </Typography>
          </Box>

          {/* FAQ toggle spots */}
          {questions.map((faq, index) => (
            <Box key={index} width="100%" mb={2}>
              <Button
                variant="outlined"
                onClick={() => handleToggleAnswer(index)}
                fullWidth
                size="large"
              >
                {faq.question}
              </Button>
              <Collapse in={expandedIndex === index}>
                <Typography variant="body1" align="center">
                  {faq.answer}
                </Typography>
              </Collapse>
            </Box>
          ))}
        </Box>

        <Box
          flexBasis={isNonMobileScreens ? "42%" : undefined}
          mt={isNonMobileScreens ? undefined : "2rem"}
        ></Box>

        {isNonMobileScreens && <Box flexBasis="26%"></Box>}
      </Box>
    </Box>
  );
};

export default HelpPage;
