import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setCourses } from "state";
import CourseWidget from "./CourseWidget";

const CoursesWidget = ({ userId, isProfile = false }) => {
  const dispatch = useDispatch();
  const courses = useSelector((state) => state.courses);
  const token = useSelector((state) => state.token);

  const [currentPage, setCurrentPage] = useState(1);

  const getCourses = async () => {
    const response = await fetch("http://localhost:3001/courses/show", {
      method: "GET",
      headers: { Authorization: `Bearer ${token}` },
    });

    const data = await response.json();
    dispatch(setCourses({ courses: data }));
  };

  const getUserCourses = async () => {
    const response = await fetch(`http://localhost:3001/users/${userId}/subscriptions`, {
      method: "GET",
      headers: { Authorization: `Bearer ${token}` },
    });

    const data = await response.json();
    dispatch(setCourses({ courses: data }));
  };

  useEffect(() => {
    if (isProfile) {
      getUserCourses();
    } else {
      getCourses();
    }
  }, []);

  const coursesPerPage = 10;
  const totalPages = Math.ceil(courses.length / coursesPerPage);

  const startIndex = (currentPage - 1) * coursesPerPage;
  const endIndex = startIndex + coursesPerPage;
  const currentCourses = courses.slice(startIndex, endIndex);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="courses-container">
      <div className="courses-widget">
        {currentCourses.map(({ _id, title, credits, termCode, subjectCode, courseID, courseDesignation, subscribed }) => (
          <CourseWidget
            key={_id}
            courseId={_id}
            title={title}
            credits={credits}
            termCode={termCode}
            subjectCode={subjectCode}
            courseID={courseID}
            courseDesignation={courseDesignation}
            subscribed={subscribed}
          />
        ))}
      </div>

      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Previous
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button onClick={handleNextPage} disabled={currentPage === totalPages}>
          Next
        </button>
      </div>
    </div>
  );
};

export default CoursesWidget;
