import { Box, useMediaQuery } from "@mui/material";
import { useSelector } from "react-redux";
import Navbar from "scenes/navbar";
import UserWidget from "scenes/widgets/UserWidget";
import Footer from "scenes/footer";
import CoursesWidget from "scenes/widgets/CoursesWidget";

const HomePage = () => {
  const isNonMobileScreens = useMediaQuery("(min-width: 1000px)");
  const { _id, picturePath } = useSelector((state) => state.user);

  return (
    <Box display="flex" flexDirection="column" minHeight="100vh">
      <Navbar />

      <Box flexGrow={1} px={2} py={4} display="flex" justifyContent="space-between">
        <Box flexBasis={isNonMobileScreens ? "30%" : "100%"} maxWidth={isNonMobileScreens ? "30%" : "100%"}>
          <UserWidget userId={_id} picturePath={picturePath} />
        </Box>

        <Box flexBasis={isNonMobileScreens ? "70%" : "100%"} maxWidth={isNonMobileScreens ? "70%" : "100%"} py={isNonMobileScreens ? 0 : 2}>
          <CoursesWidget userId={_id} isProfile={false} />
        </Box>
      </Box>

      <Footer />
    </Box>
  );
};

export default HomePage;
