// color design tokens export
export const colorTokens = {
  red: {
    50: "#FFEAE8",
    100: "#FFC4BD",
    200: "#FF9A8F",
    300: "#FF6F60",
    400: "#FF452E",
    500: "#FF1B00",
    600: "#CC1300",
    700: "#990C00",
    800: "#660600",
    900: "#330300",
  },
  white: "#FFFFFF",
  black: "#000000",
};

// mui theme settings
export const themeSettings = (mode) => {
  const primaryColor = mode === "dark" ? colorTokens.red[500] : colorTokens.red[800];
  const secondaryColor = mode === "dark" ? colorTokens.white : colorTokens.red[500];
  const backgroundDefault = mode === "dark" ? colorTokens.black : colorTokens.white;
  const backgroundAlt = mode === "dark" ? colorTokens.red[900] : colorTokens.red[50];
  const textPrimary = mode === "dark" ? colorTokens.white : colorTokens.black;
  const textSecondary = mode === "dark" ? colorTokens.red[200] : colorTokens.red[600];

  return {
    palette: {
      mode: mode,
      primary: {
        main: primaryColor,
      },
      secondary: {
        main: secondaryColor,
      },
      text: {
        primary: textPrimary,
        secondary: textSecondary,
      },
      background: {
        default: backgroundDefault,
        alt: backgroundAlt,
      },
    },
    typography: {
      fontFamily: ["Rubik", "sans-serif"].join(","),
      fontSize: 12,
      h1: {
        fontFamily: ["Rubik", "sans-serif"].join(","),
        fontSize: 40,
      },
      h2: {
        fontFamily: ["Rubik", "sans-serif"].join(","),
        fontSize: 32,
      },
      h3: {
        fontFamily: ["Rubik", "sans-serif"].join(","),
        fontSize: 24,
      },
      h4: {
        fontFamily: ["Rubik", "sans-serif"].join(","),
        fontSize: 20,
      },
      h5: {
        fontFamily: ["Rubik", "sans-serif"].join(","),
        fontSize: 16,
      },
      h6: {
        fontFamily: ["Rubik", "sans-serif"].join(","),
        fontSize: 14,
      },
    },
  };
};
