import React from "react";
import { Box, Typography, useTheme, Button } from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import WidgetWrapper from "components/WidgetWrapper";
import { useState, useEffect } from "react";
import { setSubscriptions } from "state";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const CourseWidget = ({
  courseId,
  title,
  credits,
  termCode,
  subjectCode,
  courseID,
  courseDesignation,
  subscribed,
  description,
}) => {
  const dispatch = useDispatch();
  const token = useSelector((state) => state.token);
  const loggedInUserId = useSelector((state) => state.user._id);
  const courseList = useSelector((state) => state.courseList);

  const { palette } = useTheme();
  const primary = palette.primary.main;

  const isSubscribed = courseList.some((course) => course.courseId === courseId);

  const subscribeCourse = async () => {
    const response = await fetch(
      `http://localhost:3001/courses/subscribe/${loggedInUserId}/${courseId}`,
      {
        method: "GET",
        headers: { Authorization: `Bearer ${token}` },
      }
    );
    const data = await response.json();
    dispatch(setSubscriptions({ courseList: data }));
  };

  const unsubscribeCourse = async () => {
    const response = await fetch(
      `http://localhost:3001/courses/unsubscribe/${loggedInUserId}/${courseId}`,
      {
        method: "GET",
        headers: { Authorization: `Bearer ${token}` },
      }
    );
    const data = await response.json();
    dispatch(setSubscriptions({ courseList: data }));
  };

  const handleToggleSubscription = async () => {
    if (isSubscribed) {
      // Check if the course is in the courseList before unsubscribing
      const isInCourseList = courseList.some(
        (course) => course.courseId === courseId
      );
      if (isInCourseList) {
        await unsubscribeCourse();
        toast.error("Course successfully unsubscribed", {
          autoClose: 1200,
        });
      } else {
        toast.error("Unable to unsubscribe. Course not found in subscriptions");
      }
    } else {
      await subscribeCourse();
      toast.success("Course successfully subscribed", { autoClose: 1200 });
    }
  };

  useEffect(() => {
    // No need to set `isSubscribed` here since it is determined dynamically
  }, []);

  return (
    <WidgetWrapper>
      <Box p="1rem">
        <Typography variant="h6" sx={{ color: primary }}>
          {title}
        </Typography>
        <Typography variant="body1">{`Credits: ${credits}`}</Typography>
        <Typography variant="body1">{`Course Designation: ${courseDesignation}`}</Typography>

        <Typography variant="body1" sx={{ whiteSpace: "pre-wrap" }}>
          {`\nDescription: ${description}`}
        </Typography> {/* Add the new line */}
        <Button
          variant="outlined"
          onClick={handleToggleSubscription}
          sx={{ marginTop: "1rem" }}
          disabled={!isSubscribed && subscribed === 0} // Disable if not subscribed
        >
          {isSubscribed ? "Unsubscribe" : "Subscribe"}
        </Button>
      </Box>
      <ToastContainer
        position="bottom-right"
        style={{
          fontSize: "20px",
        }}
      />
    </WidgetWrapper>
  );
};

export default CourseWidget;
