import { Box, useMediaQuery } from "@mui/material";
import { useSelector } from "react-redux";
import Navbar from "scenes/navbar";
import UserWidget from "scenes/widgets/UserWidget";
import Footer from "scenes/footer";
import CoursesWidget from "scenes/widgets/CoursesWidget";
import { useState } from "react";

const HomePage = () => {
  const isNonMobileScreens = useMediaQuery("(min-width: 1000px)");
  const { _id, picturePath } = useSelector((state) => state.user);

  const [searchText, setSearchText] = useState("");

  const handleSearchChange = (text) => {
    setSearchText(text);
  };

  return (
    <Box
      display="flex"
      flexDirection="column"
      minHeight="100vh"
    >
      <Navbar onSearch={handleSearchChange} />

      <Box flex="1" padding="2rem 6%" display="flex" flexDirection={isNonMobileScreens ? "row" : "column"} gap="0.5rem">
        <Box flexBasis={isNonMobileScreens ? "26%" : undefined}>
          <UserWidget userId={_id} picturePath={picturePath} />
        </Box>

        <Box flexBasis={isNonMobileScreens ? "42%" : undefined} mt={isNonMobileScreens ? undefined : "2rem"}>
          <CoursesWidget userId={_id} isProfile={false} searchText={searchText} />
        </Box>

        {isNonMobileScreens && <Box flexBasis="26%"></Box>}
      </Box>

      <Footer />
    </Box>
  );
};

export default HomePage;
