import React, { useState } from "react";
import { Box, Typography, useTheme, Button } from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import WidgetWrapper from "components/WidgetWrapper";
import { setSubscriptions } from "state";
import { useEffect } from "react";



const CoursesWidget = ({ userId, isProfile = false }) => {
  const dispatch = useDispatch();
  const courses = useSelector((state) => state.courses);
  const token = useSelector((state) => state.token);

  const [currentPage, setCurrentPage] = useState(1);

  const getCourses = async () => {
    const response = await fetch("http://localhost:3001/courses/show", {
      method: "GET",
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await response.json();
    dispatch(setCourses({ courses: data }));
  };

  const getUserCourses = async () => {
    const response = await fetch(`http://localhost:3001/users/${userId}/subscriptions`, {
      method: "GET",
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await response.json();
    dispatch(setCourses({ courses: data }));
  };

  useEffect(() => {
    if (isProfile) {
      getUserCourses();
    } else {
      getCourses();
    }
  }, []);

  const handleNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const handlePreviousPage = () => {
    setCurrentPage((prevPage) => prevPage - 1);
  };

  const startIndex = (currentPage - 1) * 10;
  const endIndex = startIndex + 10;
  const currentCourses = courses.slice(startIndex, endIndex);

  return (
    <>
      {currentCourses.map(({ _id, title, credits, termCode, subjectCode, courseID, courseDesignation, subscribed }) => (
        <CourseWidget
          key={_id}
          courseId={_id}
          title={title}
          credits={credits}
          termCode={termCode}
          subjectCode={subjectCode}
          courseID={courseID}
          courseDesignation={courseDesignation}
          subscribed={subscribed}
        />
      ))}
      <Box display="flex" justifyContent="center" mt={2}>
        <Button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Previous Page
        </Button>
        <Button onClick={handleNextPage} disabled={endIndex >= courses.length}>
          Next Page
        </Button>
      </Box>
    </>
  );
};

export default CoursesWidget;
