import { useState } from "react";
import { Box, Typography, Button, Collapse } from "@mui/material";
import Navbar from "scenes/navbar";

const HelpPage = () => {
  const isNonMobileScreens = useMediaQuery("(min-width: 1000px)");
  const { _id, picturePath} = useSelector((state) => state.user);
  const [expandedIndex, setExpandedIndex] = useState(null);

  const questions = [
    {
      question: "What is the purpose of this website?",
      answer:
        "The purpose of this website is to provide information about our products and services.",
    },
    {
      question: "How do I create an account?",
      answer: "To create an account, click on the 'Sign Up' button and follow the registration process.",
    },
    {
      question: "How can I contact customer support?",
      answer: "You can contact our customer support team by calling our helpline or sending an email to support@example.com.",
    },
  ];

  const handleToggleAnswer = (index) => {
    setExpandedIndex(index === expandedIndex ? null : index);
  };

  return (
    <Box
    width="100%"
    padding="2rem 6%"
    display={isNonMobileScreens ? "flex" : "block"}
    gap="0.5rem"
    justifyContent="space-between">
    <Navbar />

    <Box flexBasis={isNonMobileScreens ? "26%" : undefined}>
        <Typography variant="h4">About</Typography>
        <Typography variant="body1">
          Simplifying Course Enrollment at UW Madison.
          enRollBadge is a web app designed for UW Madison students to streamline the class enrollment process.
          Say goodbye to constant manual checks and missed opportunities.
          With enRollBadge, you'll receive real-time notifications when your desired classes become open or waitlisted.
          Simply sign in, select your classes, and let enRollBadge handle the rest.
          Experience stress-free enrollment and focus on what matters most â€“ your education.
          Join enRollBadge today and say hello to a smarter way of enrolling.
        </Typography>
      </Box>

      <Typography variant="h5" gutterBottom>
        Frequently Asked Questions
      </Typography>

      {/* Display predefined questions */}
      {questions.map((faq, index) => (
        <Box key={index} width="100%" mb="1rem">
          <Button
            variant="outlined"
            onClick={() => handleToggleAnswer(index)}
            sx={{ textTransform: "none", justifyContent: "flex-start", width: "100%" }}
          >
            <Typography variant="h6">{faq.question}</Typography>
          </Button>
          <Collapse in={index === expandedIndex}>
            <Typography>{faq.answer}</Typography>
          </Collapse>
        </Box>
      ))}
    </Box>
  );
};

export default HelpPage;
