import {
    ManageAccountsOutlined,
    WorkOutlineOutlined,
  } from "@mui/icons-material";
  import { Box, Typography, Divider, useTheme } from "@mui/material";
  import FlexBetween from "components/FlexBetween";
  import WidgetWrapper from "components/WidgetWrapper";
  import { useSelector } from "react-redux";
  import { useEffect, useState } from "react";
  import { useNavigate } from "react-router-dom";
  import { setUserSubscriptions } from "state";
  import { useDispatch } from "react-redux";


  const UserWidget = ({ userId }) => {
    const dispatch = useDispatch();
    const [user, setUser] = useState(null);
    const { palette } = useTheme();
    const navigate = useNavigate();
    const token = useSelector((state) => state.token);
    const dark = palette.neutral.dark;
    const medium = palette.neutral.medium;
    const main = palette.neutral.main;

    const getUser = async () => {
      const response = await fetch(`http://localhost:3001/users/${userId}`, {
        method: "GET",
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setUser(data);
      dispatch(setUserSubscriptions({ subscriptions: data.subscriptions }));

    };

    useEffect(() => {
      getUser();
    }, []); 

    if (!user) {
      return null;
    }

    const {
    preferredName,
    subscriptions,
    } = user;

    return (
      <WidgetWrapper>
        {/* FIRST ROW */}
        <FlexBetween
          gap="0.5rem"
          pb="1.1rem"
          onClick={() => navigate(`/profile/${userId}`)}
        >
          <FlexBetween gap="1rem">
            <Box>
              <Typography
                variant="h4"
                color={dark}
                fontWeight="500"
                sx={{
                  "&:hover": {
                    color: palette.primary.light,
                    cursor: "pointer",
                  },
                }}
              >
                {preferredName}
              </Typography>
              <Typography color={medium}>{subscriptions.length} courses subscribed.</Typography>
            </Box>
          </FlexBetween>
          <ManageAccountsOutlined />
        </FlexBetween>

        <Divider />

        {/* SECOND ROW */}
        <Box p="1rem 0">
        <Box display="flex" alignItems="center" gap="1rem">
          <WorkOutlineOutlined fontSize="large" sx={{ color: main }} />
          <Typography color={medium}>Your subscribed courses:</Typography>
        </Box>
        {subscriptions.map((subscription) => (
    <Typography key={subscription._id} color={medium}>
      {subscription.title}
    </Typography>
  ))}

      </Box>

      <Divider />



      </WidgetWrapper>
    );
  };

  export default UserWidget;
