import { Box, useMediaQuery } from "@mui/material";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import Navbar from "scenes/navbar";
import CoursesWidget from "scenes/widgets/CoursesWidget";
import UserWidget from "scenes/widgets/UserWidget";

const ProfilePage = () => {
  const [user, setUser] = useState(null);
  const { userId } = useParams();
  const token = useSelector((state) => state.token);
  const isNonMobileScreens = useMediaQuery("(min-width:1000px)");

  const getUser = async () => {
    const response = await fetch(`http://localhost:3001/users/${userId}`, {
      method: "GET",
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await response.json();
    setUser(data);
  };

  useEffect(() => {
    getUser();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  if (!user) return null;

  const currUser = useSelector((state) => state.user);

  const hasSubscribedCourses = user?.courses?.length > 0;

  return (
    <Box>
      <Navbar />
      <Box
        width="100%"
        padding="2rem 6%"
        display="flex"
        flexDirection={isNonMobileScreens ? "row" : "column"}
        gap="2rem"
        justifyContent="center"
      >
        <Box flex={1}>
          <UserWidget userId={userId} />
          <Box m="2rem 0" />
          {!hasSubscribedCourses && <p>No subscribed courses.</p>}
        </Box>
        <Box flex={1} mt={isNonMobileScreens ? 0 : "2rem"}>
          <Box m="2rem 0" />
          {hasSubscribedCourses && <CoursesWidget userId={userId} isProfile />}
        </Box>
      </Box>
    </Box>
  );
};

export default ProfilePage;
